import styled from "styled-components";

const Container = styled.section`
  width: 18rem;
`;

const Image = styled.img`
  width: 100%;
  height: 17.5rem;
  border-radius: 0.4375rem;
  object-fit: cover;
`;

const PostInfoContainer = styled.article`
  display: flex;
  flex-direction: column;
  align-items: center;
`;

const Category = styled.article`
  font-family: "Varela Round", sans-serif;
  font-size: 11px;
  color: #be9656;
  line-height: 20px;
  margin-top: 15px;
  margin-right: 10px;
  cursor: pointer;
`;

const CategoryTitle = styled.span``;

const PostTitle = styled.span`
  font-family: "Josefin Sans", sans-serif;
  font-size: 24px;
  font-weight: 700;
  margin-top: 15px;
  cursor: pointer;
`;

const PostDate = styled.span`
  font-family: "Lora", serif;
  font-style: italic;
  font-size: 13px;
  color: #999;
  margin-top: 15px;
`;

const Description = styled.p`
  font-family: "Varela Round", sans-serif;
  font-size: 14px;
  color: #444;
  line-height: 24px;
  margin-top: 15px;
  overflow: hidden;
  text-overflow: ellipsis;
  display: -webkit-box;
  -webkit-line-clamp: 4;
  -webkit-box-orient: vertical;
`;

const Post = () => {
  return (
    <Container>
      <Image
        src="https://wallpapersmug.com/download/1366x768/a91749/jinx-arcane-artwork.jpg"
        alt="postImage"
      />
      <PostInfoContainer>
        <Category>
          <CategoryTitle>Music</CategoryTitle>
          <CategoryTitle>Life</CategoryTitle>
        </Category>
        <PostTitle>Star Trek </PostTitle>
        <PostDate>15th Feb 2022</PostDate>
      </PostInfoContainer>
      <Description>
        Lorem Ipsum is simply dummy text of the printing and typesetting
        industry. Lorem Ipsum has been the industry's standard dummy text ever
        since the 1500s, when an unknown printer took a galley of type and
        scrambled it to make a type specimen book. It has survived not only five
        centuries, but also the leap into electronic typesetting, remaining
        essentially unchanged. It was popularised in the 1960s with the release
        of Letraset sheets containing Lorem Ipsum passages, and more recently
        with desktop publishing software like Aldus PageMaker including versions
        of Lorem Ipsum.
      </Description>
    </Container>
  );
};

export default Post;
