import styled from "styled-components";

const Container = styled.main``;

const Wrapper = styled.section``;

const SettingTitle = styled.article``;

const UpdateTitle = styled.span``;

const DeleteTitle = styled.span``;

const SettingForm = styled.form``;

const Label = styled.label``;

const SettingProfile = styled.article``;

const Image = styled.img``;

const FileInput = styled.input``;

const TextInput = styled.input``;

const Email = styled.input``;

const Password = styled.input``;

const Button = styled.button``;

const Settings = () => {
  return (
    <Container>
      <Wrapper>
        <SettingTitle>
          <UpdateTitle>Update Your Account</UpdateTitle>
          <DeleteTitle>Delete Account</DeleteTitle>
        </SettingTitle>
        <SettingForm>
          <Label>Profile Picture</Label>
          <SettingProfile>
            <Image src="" alt="profileImg" />
            <Label htmlFor="fileInput">
              <i className="settingsPPIcon far fa-user-circle"></i>
            </Label>
            <FileInput
              type={"file"}
              id="fileInput"
              style={{ display: "none" }}
            />
          </SettingProfile>
          <Label>Username</Label>
          <TextInput type={"text"} placeholder="Username" />
          <Label>Email</Label>
          <Email type={"email"} placeholder="email@email.com" />
          <Label>Password</Label>
          <Password type={"password"} />
          <Button>Update</Button>
        </SettingForm>
      </Wrapper>
    </Container>
  );
};

export default Settings;
