import { NavLink } from "react-router-dom";
import styled from "styled-components";

const Container = styled.section`
  display: flex;
  width: 100%;
  align-items: center;
  font-family: "Josefin Sans", sans-serif;
  position: sticky;
  top: 0;
  z-index: 999;
  height: 3.125rem;
  background-color: white;
`;

const TopLeft = styled.article`
  flex: 3;
  display: flex;
  align-items: center;
  justify-content: center;
`;

const TopCenter = styled.article`
  flex: 6;
`;

const NavList = styled.ul`
  display: flex;
  justify-content: center;
`;

const NavItem = styled.li`
  margin-right: 1.25rem;
  font-weight: 300;
  font-size: 1.125rem;
  cursor: pointer;
  &:hover {
    font-weight: 400;
  }
`;

const TopRight = styled.article`
  flex: 3;
  display: flex;
  align-items: center;
  justify-content: center;
`;

const TopImage = styled.img`
  width: 2.5rem;
  height: 2.5rem;
  object-fit: cover;
  border-radius: 50%;
  cursor: pointer;
`;

const Icon = styled.div`
  display: flex;
  align-items: center;
  justify-content: center;
  margin-left: 1rem;
  cursor: pointer;
`;

const TopBar = () => {
  return (
    <Container>
      <TopLeft>
        <NavLink to={`/`}>
          <Icon>
            <i class="fab fa-reddit-alien"></i>
          </Icon>
        </NavLink>
        <NavLink to={`/`}>
          <Icon>
            <i className="sidebarIcon fab fa-twitter"></i>
          </Icon>
        </NavLink>
        <NavLink to={`/`}>
          <Icon>
            <i class="fab fa-linkedin"></i>
          </Icon>
        </NavLink>
        <NavLink to={`/`}>
          <Icon>
            <i className="sidebarIcon fab fa-instagram"></i>
          </Icon>
        </NavLink>
      </TopLeft>
      <TopCenter>
        <NavList>
          <NavLink to={`/`}>
            <NavItem> HOME </NavItem>
          </NavLink>
          <NavLink to={`/about`}>
            <NavItem> ABOUT </NavItem>
          </NavLink>
          <NavLink to={`/compose`}>
            <NavItem> COMPOSE </NavItem>
          </NavLink>
        </NavList>
      </TopCenter>
      <TopRight>
        <NavList>
          <NavLink to={`/login`}>
            <NavItem> LOGIN </NavItem>
          </NavLink>
          <NavLink to={`/register`}>
            <NavItem> REGISTER </NavItem>
          </NavLink>
        </NavList>
        <NavLink to={`/settings`}>
          <TopImage src="https://www.gizmodo.com.au/wp-content/uploads/sites/2/2021/11/04/league-of-legends-arcane.jpg?quality=80&resize=1280,720" />
        </NavLink>
        <i
          className="topSearchIcon fas fa-search"
          style={{
            fontSize: "1.125rem",
            color: "#666",
            cursor: "pointer",
            marginLeft: "1rem",
          }}
        ></i>
      </TopRight>
    </Container>
  );
};

export default TopBar;
